class Brand{constructor(e){this.ID=parseInt(e.ID)||0,this.Name=e.Name??"",this.Level=parseInt(e.Level)??0,this.ParentID=parseInt(e.Parent_ID)||0,this.CrueltyFree=1===e.Cruelty_Free,this.AnimalTesting=1===e.Animal_Testing}static getAll(e){return new Promise(s=>Ajax.Post("brands",{body:{query:e},success:{ok:e=>{s(e.body.map(e=>Object.assign(new Brand({}),{Name:e.Name,ID:e.ID})))},any:e=>{e.ok||s([])}}}))}}